import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  constructor() {
    var HoverButton = React.createClass({
      getInitialState: function () {
          return {hover: false};
      },

      mouseOver: function () {
          this.setState({hover: true});
      },

      mouseOut: function () {
          this.setState({hover: false});
      },

      render: function() {
          var label = "foo";
          if (this.state.hover) {
              label = "bar";
          }
          return React.createElement(
              "button",
              {onMouseOver: this.mouseOver, onMouseOut: this.mouseOut},
              label
          );
      }
  });

  React.render(React.createElement(HoverButton, null), document.body);
  }

  render() {
    return (
      <button 
        className="square"
        onClick={this.props.onClick}
      >

        { this.props.value}

      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    let x = [];
    this.state = {
      Value: Array(10).fill(null),
    }
  }
  func(i) {
    // Expand upon this
    const x = this.state.Value.slice();
    x[i] = Math.random(0.1, 1) * 100;
    this.setState({Value: x})
  }

  createTable = () => {
    let x = [];
    for(let i = 0; i < 250; i++) {
      x.push(<Square value={this.state.Value[i]} onClick={() => this.func(i)}/>); // Props will be passed from here
    }
    return x;
  }

  render() {
  return (<div>{this.createTable()}</div>)
  };

}

function App() {
  return (
    <div className="App">
      <CustomRendererForGrids />
    </div>
  );
}

export default App;
