import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      value: null,
    };
  }

  render() {
    return (
      <button 
        className="square"
        onClick={this.props.onClick}
      >

        { this.props.value}

      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    let x = [];
    this.state = {
      Value: 0,
    }
  }
  func(i) {
    // Expand upon this
    this.value++;

    //alert("Hello");
  }

  createTable = () => {
    let x = [];
    for(let i = 0; i < 10; i++) {
      x.push(<Square value={this.state.Value} onClick={() => this.func(i)}/>); // Props will be passed from here
    }
    return x;
  }

  render() {
  return (<div>{this.createTable()}</div>)
  };

}





function Prototype() {
  let x = []
  for(let i = 0; i < 1; i++) {
    x.push(<article><CustomRendererForGrids /></article>);
  }
  //alert("Hello");
  return (<div>{x}</div>);
}

function App() {
  return (
    <div className="App">
      <Prototype />
    </div>
  );
}

export default App;
