import React from 'react';
import logo from './logo.svg';
import './App.css';
import Decor from './decor.js';
import { Button } from 'antd';
class Square extends React.Component {

  render() {
    return (
      <button 
        className="square"
        onClick={this.props.onClick}
      >

        { this.props.value}

      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Value: Array(15).fill().map(() => Array(15).fill(1)),
    }
  }
  func(i, j) {
    // Expand upon this work is preety unsatisfactory
    const squares = this.state.Value.slice();
    squares[i][j] = 0;
    //alert(i + " " + j); // Works as expected
    this.setState({squares: squares});
  }

  createTable = () => {
    let x = [];
    for(let i = 0; i < 15; i++) {
      for(let j = 0; j < 15; j++) {
        x.push(<Square value={this.state.Value[i][j]} onClick={() => this.func(i, j)}/>);
      }
      x.push(<div />)
    }
    return x;
  }

  render() {
  return (<div>{this.createTable()}<desApp /></div>)
  };

}

function App() {
  return (
    <div className="App">
      <button type="primary"></button>
      <Decor />
      <CustomRendererForGrids />
    </div>
  );
}

export default App;
