import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      value: null,
    };
  }

  render() {
    return (
      <button className="square">
        {this.state.Value}
      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    let x = [];
  }

  createTable = () => {
    for(let i = 0; i < 10; i++) {
      this.x.push(<Square value={i}/>); // Props will be passed from here
    }
  }
  render() {
  return (<div>{this.x}</div>)
  };
  }





function Prototype() {
  let x = []
  for(let i = 0; i < 1; i++) {
    x.push(<article><CustomRendererForGrids /></article>);
  }
  //alert("Hello");
  return (<div>{x}</div>);
}

function App() {
  return (
    <div className="App">
      <CustomRendererForGrids />
    </div>
  );
}

export default App;
