import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  render() {
    return (
      <button 
        className="square"
        onClick={this.props.onClick}
      >

        { this.props.value}

      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    let x = [];
    this.state = {
      Value: Array(10).fill(null),
    }
  }
  func(i) {
    // Expand upon this
    const x = this.state.Value.slice();
    x[i] = Math.random(0.1, 1) * 100;
    this.setState({Value: x})
  }

  createTable = () => {
    let x = [];
    for(let i = 0; i < 10; i++) {
      for(let j = 0; j < 2; j++) {
        x.push(<Square value={this.state.Value[j]} onClick={() => this.func(i)}/>); // Props will be passed from here
      }
      //x.push()
    }
    return x;
  }

  render() {
  return (<div>{this.createTable()}</div>)
  };

}

function App() {
  return (
    <div className="App">
      <CustomRendererForGrids />
    </div>
  );
}

export default App;
