import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      value: null,
    };
  }

  render() {
    return (
      <button className="square" onClick={this.stateFlip()}>
        {this.state.Value}
      </button>
    );
  }
}

function CustomRendererForGrids() {
  let x = []
  for(let i = 0; i < 1; i++) {
    x.push(<Square value={i}/>); // Props will be passed from here
  }
  return (<div>{x}</div>);
}

function Prototype() {
  let x = []
  for(let i = 0; i < 1; i++) {
    x.push(<article><CustomRendererForGrids /></article>);
  }
  //alert("Hello");
  return (<div>{x}</div>);
}

function App() {
  return (
    <div className="App">
      <Prototype />
    </div>
  );
}

export default App;
