import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  render() {
    return (
      <button 
        className="square"
        onClick={this.props.onClick}
      >

        { this.props.value}

      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Value: [15][15],
    }
  }
  func(i, j) {
    // Expand upon this
    let x = this.state.Value[15][15];
    x[i][j] = Math.random(0.1, 1) * 100;
    this.setState({Value: x})
  }

  createTable = () => {
    let x = [];
    for(let i = 0; i < 15; i++) {
      for(let j = 0; j < 15; j++) {
        x.push(<Square value={this.state.Value[i]} onClick={() => this.func(i, j)}/>); // Props will be passed from hfuncere
      }
      x.push(<div />)
    }
    return x;
  }

  render() {
  return (<div>{this.createTable()}</div>)
  };

}

function App() {
  return (
    <div className="App">
      <CustomRendererForGrids />
    </div>
  );
}

export default App;
