import React from 'react';
import logo from './logo.svg';
import './App.css';

class Square extends React.Component {

  render() {
    return (
      <button 
        className="square"
        onClick={this.props.onClick}
      >

        { this.props.value}

      </button>
    );
  }
}

class CustomRendererForGrids extends React.Component {
  constructor(props) {
    super(props);
    let x = [];
    this.state = {
      Value: Array(10).fill(null),
    }
  }
  func(i) {
    // Expand upon this
    let x = this.state.Value.slice();
    x[i] = 1;
    this.setState({Value: x})
    //this.setState({Value[i]: 1});
  }

  createTable = () => {
    let x = [];
    for(let i = 0; i < 10; i++) {
      x.push(<Square value={this.state.Value} onClick={() => this.func(i)}/>); // Props will be passed from here
    }
    return x;
  }

  render() {
  return (<div>{this.createTable()}</div>)
  };

}

function App() {
  return (
    <div className="App">
      <CustomRendererForGrids />
    </div>
  );
}

export default App;
